	var chapterName = "<h1 class='Texth3' id='chapterName' style='margin-bottom:32px;'>" + buttonValue +"</h1>"
	var buttonClass = document.getElementsByClassName("basicButton");
	//将滚动条重置到顶部
	document.getElementById("textBox").scrollTop = 0;
